const getOffset = (el) => {
    let x = 0;
    let y = 0;
    while (el &&
        el.offsetParent &&
        !lodash.isNaN(el.offsetLeft) &&
        !lodash.isNaN(el.offsetTop)) {
        x += el.offsetLeft - el.scrollLeft;
        y += el.offsetTop - el.scrollTop;
        const tmp = el.offsetParent;
        if (tmp) {
            // eslint-disable-next-line no-param-reassign
            el = tmp;
        }
    }
    return { top: y, left: x };
};
