const clickHandler = (elem, clickFunction, holdFunction) => {
    let longpress = false;
    let presstimer = null;
    const cancel = (e) => {
        e.stopPropagation();
        if (presstimer !== null) {
            clearTimeout(presstimer);
            presstimer = null;
        }
    };
    const click = (e) => {
        e.stopPropagation();
        if (presstimer !== null) {
            clearTimeout(presstimer);
            presstimer = null;
        }
        if (longpress) {
            return false;
        }
        clickFunction(e);
        return true;
    };
    const start = (e) => {
        e.stopPropagation();
        if (e.type === 'click' && e.button !== 0) {
            return;
        }
        longpress = false;
        presstimer = setTimeout(() => {
            holdFunction(e);
            longpress = true;
        }, 1000);
    };
    elem.addEventListener('mousedown', start);
    elem.addEventListener('touchstart', start);
    elem.addEventListener('click', click);
    elem.addEventListener('mouseout', cancel);
    elem.addEventListener('touchend', cancel);
    elem.addEventListener('touchleave', cancel);
    elem.addEventListener('touchcancel', cancel);
};
